(window.webpackJsonp=window.webpackJsonp||[]).push([[82],{525:function(n,t,s){"use strict";s.r(t);var r=s(36),e=Object(r.a)({},(function(){var n=this,t=n._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h2",{attrs:{id:"事业发展-追求希望"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#事业发展-追求希望"}},[n._v("#")]),n._v(" 事业发展，追求希望")]),n._v(" "),t("p",[n._v("  今天在这里，主要是讨论在我专注什么领域发展。因为常常感觉很累，回顾一下，是自己跑偏了呢，还是瞎忙。\n”我是一名软件开发工程师，日常主要专注于Web网络数据采集、数据存储分析、数据可视化相关业务。”，就这句话来说，\n是有点夸大，回顾过去的10年里，初入软件开发专业，到这个行业发展，已经从最初一名小兵，干到了一位有着相当经验的\n老鸟了，虽然还不是那么成熟，但是我总是在寻找原因。在哪方面，自己还是比较苍白。实际上，应该在很多方面都是，只是\n我自己不清楚。前端开发工程师，这个前端该怎么划定，你又该如何评价这种成果。特别是在成果评价上，我常常觉得自己干的\n都是无用功。这一点有点妄自菲薄。就以自己目前做的项目为例，做的哪些系统，为客户提供了数据采集、传输、存储相关的服务。\n应该是软件加服务。上面还是比较大的业务方面，还没牵扯到具体的行业。我应该有一套系统业务领域知识，并拥有支持这种知识\n的业务系统作为支撑，然后将这种领域知识与业务系统在实际工作中加以熟练运用。那么，我就可以不违心的说，我擅长这个领域。")]),n._v(" "),t("p",[n._v("领域知识 + 业务系统 + 实践运用 = 领域技术人才"),t("br"),n._v("\n多年、多行业（领域知识+业务系统+实践运用）+ 影响力（知识分享的论文、报告数） = 领域专家")]),n._v(" "),t("p",[n._v("以信管专业为例：\n信息资源管理行业：基础概念 + 企业信管领域 + 政府信管领域 = 信管领域技术人才\n图书馆行业：\n情报行业：\n档案行业：")]),n._v(" "),t("p",[n._v("该行业下，会从教育输入人才，行业吸收人才，人才应用创造价值。社会是复杂的，每个阶段都是互相交织在一起的，才会有这样\n一个社会。")])])}),[],!1,null,null,null);t.default=e.exports}}]);